import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';
import FileViewer from '../components/FileViewer';

const CampaignForm = () => {
  const navigate = useNavigate();
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [loading, setLoading] = useState(true);
  const [form, setForm] = useState({
    fullName: '',
    email: '',
    plan: '', // plan is used to control video link visibility
    role: '',
    beneficiaryName: '',
    goalAmount: '',
    donationLink: '',
    videoLink: '', // added video link field
    liabilityAgreement: false,
    privacyPolicy: false,
    serviceAgreement: false,
  });
  const [files, setFiles] = useState({ idDoc: null, billDoc: null, campaignDoc: null });
  const [success, setSuccess] = useState(false);

  // âœ… Check for login and redirect if missing
  useEffect(() => {
    const token = localStorage.getItem('token');
    console.log("CampaignForm: token on load:", token);
    if (!token) {
      navigate('/login');
      return;
    }

    const fetchAccess = async () => {
      try {
        const res = await api.get('/auth/me'); // This must return user's email
        console.log("CampaignForm: /auth/me response:", res.data);
        const email = res.data.user.email;

        const check = await api.post('/stripe/check-paid', { email });
        console.log("CampaignForm: /stripe/check-paid response:", check.data);
        if (check.data.paid) {
          setIsLoggedIn(true);
          const plan = (check.data.plan || '').toLowerCase();
          setForm((prev) => ({ ...prev, email, plan }));
        } else {
          alert('ðŸš« You must purchase a plan first.');
          navigate('/pricing');
        }
      } catch (err) {
        console.error("CampaignForm: error in fetchAccess", err);
        alert('Something went wrong. Please login again.');
        navigate('/login');
      } finally {
        setLoading(false);
      }
    };

    fetchAccess();
  }, [navigate]);

  if (loading) return <p>Loading...</p>;

  const handleChange = (e) => {
    const { name, type, checked, value } = e.target;
    setForm({ ...form, [name]: type === 'checkbox' ? checked : value });
  };

  const handleFile = (e) => {
    setFiles({ ...files, [e.target.name]: e.target.files[0] });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!form.liabilityAgreement || !form.privacyPolicy || !form.serviceAgreement) {
      alert('You must agree to all the agreements before submitting.');
      return;
    }

    const data = new FormData();
    Object.entries(form).forEach(([key, value]) => data.append(key, value));
    Object.entries(files).forEach(([key, value]) => data.append(key, value));

    try {
      await api.post('/campaign', data);
      setSuccess(true);
      // Redirect to dashboard after successful submission
      navigate('/dashboard');
    } catch (err) {
      alert('Submission failed');
    }
  };

  if (!isLoggedIn) return null;

  return (
    <div className="p-6 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Register Campaign</h1>
      {success ? (
        <p className="text-green-600">âœ… Submitted! Youâ€™ll receive links in 24â€“48 hrs.</p>
      ) : (
        <form onSubmit={handleSubmit} className="grid gap-4">
          <input name="fullName" placeholder="Full Name" onChange={handleChange} required className="p-2 border" />
          <input name="email" placeholder="Email" type="email" onChange={handleChange} required className="p-2 border" />
          <select name="role" onChange={handleChange} required className="p-2 border">
            <option value="">Select Role</option>
            <option value="author">Author</option>
            <option value="beneficiary">Beneficiary</option>
            <option value="both">Both</option>
          </select>
          {form.role === 'author' && (
            <input name="beneficiaryName" placeholder="Beneficiary Name" onChange={handleChange} required className="p-2 border" />
          )}
          <input name="goalAmount" placeholder="Goal Amount (USD)" type="number" onChange={handleChange} required className="p-2 border" />
          <input name="donationLink" placeholder="Donation Link" type="url" onChange={handleChange} required className="p-2 border" />

          {(form.plan === 'standard' || form.plan === 'video') && (
            <input
              name="videoLink"
              placeholder="Video Link"
              type="url"
              onChange={handleChange}
              required
              className="p-2 border"
            />
          )}

          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              id="liabilityAgreement"
              name="liabilityAgreement"
              checked={form.liabilityAgreement}
              onChange={handleChange}
              required
            />
            <label htmlFor="liabilityAgreement" className="text-sm">
              I agree to the Liability Indemnity Agreement (<FileViewer fileUrl="/api/agreements/liablity.docx" fileName="Liability Agreement" />)
            </label>
          </div>

          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              id="privacyPolicy"
              name="privacyPolicy"
              checked={form.privacyPolicy}
              onChange={handleChange}
              required
            />
            <label htmlFor="privacyPolicy" className="text-sm">
              I agree to the Privacy Policy (<FileViewer fileUrl="/api/agreements/privacy.docx" fileName="Privacy Policy" />)
            </label>
          </div>

          <div className="flex items-center space-x-2">
            <input
              type="checkbox"
              id="serviceAgreement"
              name="serviceAgreement"
              checked={form.serviceAgreement}
              onChange={handleChange}
              required
            />
            <label htmlFor="serviceAgreement" className="text-sm">
              I agree to the Service Agreement (<FileViewer fileUrl="/api/agreements/service.pdf" fileName="Service Agreement" />)
            </label>
          </div>

          <div>
            <label>ID Document:</label>
            <input name="idDoc" type="file" accept=".pdf,.jpg,.png" onChange={handleFile} required className="p-2" />
          </div>
          <div>
            <label>Utility Bill:</label>
            <input name="billDoc" type="file" accept=".pdf,.jpg,.png" onChange={handleFile} required className="p-2" />
          </div>
          <div>
            <label>Campaign Doc (Word):</label>
            <input name="campaignDoc" type="file" accept=".doc,.docx" onChange={handleFile} required className="p-2" />
          </div>

          <button type="submit" className="bg-green-600 text-white p-2 rounded">Submit Registration</button>
        </form>
      )}
    </div>
  );
};

export default CampaignForm;
