import React, { useEffect, useState } from 'react';
import api from '../utils/api';
import FileViewer from '../components/FileViewer';

const AdminPanel = () => {
  const [campaigns, setCampaigns] = useState([]);

  useEffect(() => {
    const fetchAll = async () => {
      try {
        const res = await api.get('/campaign/all');
        setCampaigns(res.data);
      } catch (err) {
        console.error('Error loading campaigns');
      }
    };
    fetchAll();
  }, []);

  const updateStatus = async (id, status) => {
    try {
      if (status === 'approved') {
        await api.patch(`/campaign/${id}/approve`);
      } else if (status === 'rejected') {
        await api.patch(`/campaign/${id}/reject`);
      }
      setCampaigns(campaigns.map(c => c._id === id ? { ...c, status } : c));
    } catch {
      alert('Failed to update status');
    }
  };

  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">Admin Panel</h1>
      {campaigns.length === 0 ? (
        <p>No campaigns found.</p>
      ) : (
        <div className="overflow-auto">
          <table className="w-full border text-sm">
            <thead>
              <tr className="bg-gray-200">
                <th className="p-2">User</th>
                <th className="p-2">Email</th>
                <th className="p-2">Goal</th>
                <th className="p-2">Status</th>
                <th className="p-2">Actions</th>
                <th className="p-2">Files</th>
              </tr>
            </thead>
            <tbody>
              {campaigns.map((c) => (
                <tr key={c._id} className="border-t">
                  <td className="p-2">{c.fullName}</td>
                  <td className="p-2">{c.email}</td>
                  <td className="p-2">${c.goalAmount}</td>
                  <td className="p-2">{c.status}</td>
                  <td className="p-2">
                    <button onClick={() => updateStatus(c._id, 'approved')} className="bg-green-500 text-white px-2 py-1 rounded mr-2">Approve</button>
                    <button onClick={() => updateStatus(c._id, 'rejected')} className="bg-red-500 text-white px-2 py-1 rounded">Reject</button>
                  </td>
                  <td className="p-2 space-y-1">
                    <FileViewer fileUrl={`http://localhost:5000/uploads/${c.files.idDoc}`} fileName="ID Document">ID</FileViewer>
                    <FileViewer fileUrl={`http://localhost:5000/uploads/${c.files.billDoc}`} fileName="Utility Bill">Bill</FileViewer>
                    <FileViewer fileUrl={`http://localhost:5000/uploads/${c.files.campaignDoc}`} fileName="Campaign Doc">Doc</FileViewer>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      )}
    </div>
  );
};

export default AdminPanel;
