import React from 'react';
import api from '../utils/api';
import { useNavigate } from 'react-router-dom';

const Pricing = ({ user }) => {
  const navigate = useNavigate();

  const handleCheckout = async (plan) => {
    if (!user?.email) return navigate('/login');

    const email = user.email;
    try {
      const res = await api.post('/stripe/create-checkout-session', {
        plan,
        email,
      });
      window.location.href = res.data.url;
    } catch (err) {
      console.error(err);
      alert('Failed to create checkout session');
    }
  };

  return (
    <div className="p-6">
      <h2 className="text-3xl font-bold mb-4">Choose Your Plan</h2>
      <div className="grid grid-cols-1 sm:grid-cols-3 gap-4">
        {['basic', 'standard', 'video'].map((plan) => (
          <div key={plan} className="border p-4 rounded shadow">
            <h3 className="text-xl font-semibold capitalize">{plan} Plan</h3>
            <p className="my-2">Price: ${plan === 'standard' ? 40 : 20}</p>
            <button
              className="bg-green-600 text-white px-4 py-2 rounded"
              onClick={() => handleCheckout(plan)}
            >
              Buy Now
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Pricing;
