import React from 'react';
import api from '../utils/api';

const plans = [
  { name: 'Basic Plan', id: 'basic', price: '$20', description: '2 custom campaign pages.' },
  { name: 'Standard Plan', id: 'standard', price: '$40', description: 'Includes campaign + custom video.' },
  { name: 'Video Only', id: 'video', price: '$20', description: 'Standalone video based on script.' },
];

const Pricing = () => {
  const handleCheckout = async (planId) => {
    try {
      const res = await api.post('/stripe/create-checkout-session', { plan: planId });
      window.location.href = res.data.url;
    } catch (err) {
      alert('Checkout failed');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 p-10">
      <h1 className="text-3xl font-bold text-center mb-8">Choose Your Plan</h1>
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 max-w-5xl mx-auto">
        {plans.map((plan) => (
          <div key={plan.id} className="bg-white rounded-lg shadow-md p-6 flex flex-col justify-between">
            <div>
              <h2 className="text-xl font-semibold mb-2">{plan.name}</h2>
              <p className="text-gray-700 mb-4">{plan.description}</p>
              <p className="text-green-600 font-bold text-lg">{plan.price}</p>
            </div>
            <button
              onClick={() => handleCheckout(plan.id)}
              className="mt-4 bg-green-600 text-white py-2 rounded w-full hover:bg-green-700"
            >
              Select Plan
            </button>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Pricing;
