import React, { useEffect, useState } from 'react';
import api from '../utils/api';

const Dashboard = () => {
  const [campaign, setCampaign] = useState(null);

  useEffect(() => {
    const fetchCampaign = async () => {
      try {
        const res = await api.get('/campaign/my');
        setCampaign(res.data);
      } catch (err) {
        console.error('Error fetching campaign');
      }
    };
    fetchCampaign();
  }, []);

  if (!campaign) return <p className="p-6">No campaign submitted yet.</p>;

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Your Campaign Details</h1>
      <div className="bg-white rounded shadow p-4">
        <p><strong>Full Name:</strong> {campaign.fullName}</p>
        <p><strong>Email:</strong> {campaign.email}</p>
        <p><strong>Role:</strong> {campaign.role}</p>
        {campaign.beneficiaryName && <p><strong>Beneficiary:</strong> {campaign.beneficiaryName}</p>}
        <p><strong>Goal Amount:</strong> ${campaign.goalAmount}</p>
        <p><strong>Donation Link:</strong> <a className="text-blue-500 underline" href={campaign.donationLink} target="_blank" rel="noreferrer">{campaign.donationLink}</a></p>
        <p><strong>Status:</strong> {campaign.status}</p>

        <div className="mt-4">
          <p><strong>Uploaded Files:</strong></p>
          <ul className="list-disc ml-6">
            <li><a href={campaign.idDoc} target="_blank" rel="noreferrer" className="text-blue-600">ID Document</a></li>
            <li><a href={campaign.billDoc} target="_blank" rel="noreferrer" className="text-blue-600">Utility Bill</a></li>
            <li><a href={campaign.campaignDoc} target="_blank" rel="noreferrer" className="text-blue-600">Campaign Word Doc</a></li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
