import React, { useEffect, useState } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import api from "../utils/api";

const PaymentSuccess = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const [error, setError] = useState(null);

  useEffect(() => {
    const query = new URLSearchParams(location.search);
    const email = query.get("email");
    const plan = query.get("plan");

    if (!email || !plan) {
      setError("Missing payment information.");
      return;
    }

    const recordPayment = async () => {
      try {
        await api.get(`/stripe/record-payment?email=${encodeURIComponent(email)}&plan=${plan}`);
        // After recording payment, reload user state by calling /auth/me
        await api.get("/auth/me");
        navigate("/register-campaign");
      } catch (err) {
        setError("Failed to record payment. Please contact support.");
      }
    };

    recordPayment();
  }, [location.search, navigate]);

  if (error) {
    return <div className="p-6 text-red-600">{error}</div>;
  }

  return <div className="p-6">Processing your payment, please wait...</div>;
};

export default PaymentSuccess;
