import React, { useState,useEffect } from 'react';
import { useAuth } from '../context/AuthContext';
import { useNavigate,useLocation } from 'react-router-dom';

const Login = () => {
useEffect(() => {
  const token = localStorage.getItem('token');
  if (token) navigate('/dashboard'); // or /dashboard
}, []);
  const { login } = useAuth();
  const navigate = useNavigate();
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const location = useLocation();
const from = location.state?.from || '/dashboard'; // default fallback


    try {
      await login(email, password);
      // After successful login or register:
        navigate(from);
    } catch (err) {
      setError('Invalid credentials');
    }
  };

  return (
    <div className="min-h-screen flex items-center justify-center bg-gray-100">
      <form onSubmit={handleSubmit} className="bg-white p-6 rounded shadow-md w-full max-w-sm">
        <h2 className="text-2xl font-bold mb-4">Login</h2>
        {error && <p className="text-red-500 text-sm mb-2">{error}</p>}
        <input type="email" value={email} onChange={(e) => setEmail(e.target.value)}
               placeholder="Email" className="w-full p-2 border mb-3" required />
        <input type="password" value={password} onChange={(e) => setPassword(e.target.value)}
               placeholder="Password" className="w-full p-2 border mb-3" required />
        <button type="submit" className="w-full bg-green-600 text-white py-2 rounded">Login</button>
      </form>
    </div>
  );
};

export default Login;
