import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import api from '../utils/api';

const Register = () => {
  const navigate = useNavigate();
  const [form, setForm] = useState({
    name: '',
    email: '',
    password: '',
  });
  const [allowed, setAllowed] = useState(false);
  const [loading, setLoading] = useState(true);

  // Autofill email from ?email= query param (post-payment)
  useEffect(() => {
    const query = new URLSearchParams(window.location.search);
    const emailFromStripe = query.get('email');
    if (emailFromStripe) {
      setForm((prev) => ({ ...prev, email: emailFromStripe }));

      // ðŸ” Check backend if user has paid
      api.post('/stripe/check-paid', { email: emailFromStripe })
        .then((res) => {
          if (res.data.paid) setAllowed(true);
          else alert('âš ï¸ This email is not associated with any payment.');
        })
        .catch(() => alert('âŒ Error verifying payment.'))
        .finally(() => setLoading(false));
    } else {
      alert('Missing email from payment.');
      setLoading(false);
    }
  }, []);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const res = await api.post('/auth/register', form);
      localStorage.setItem('token', res.data.token);
      navigate('/register-campaign');
    } catch (err) {
      alert('Registration failed');
    }
  };

  if (loading) return <p className="p-4">Checking payment status...</p>;
  if (!allowed) return <p className="text-red-600 p-4">Access denied. Please complete your payment first.</p>;

  return (
    <div className="p-6 max-w-md mx-auto">
      <h2 className="text-2xl font-bold mb-4">Create Your Account</h2>
      <form onSubmit={handleSubmit} className="grid gap-4">
        <input
          name="name"
          placeholder="Full Name"
          value={form.name}
          onChange={handleChange}
          className="p-2 border"
          required
        />
        <input
          name="email"
          type="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          className="p-2 border"
          required
        />
        <input
          name="password"
          type="password"
          placeholder="Password"
          value={form.password}
          onChange={handleChange}
          className="p-2 border"
          required
        />
        <button type="submit" className="bg-blue-600 text-white p-2 rounded">
          Register & Continue
        </button>
      </form>
    </div>
  );
};

export default Register;
