import React, { useEffect, useState } from 'react';
import api from '../utils/api';
import FileViewer from '../components/FileViewer';

const Dashboard = () => {
  const [campaigns, setCampaigns] = useState([]);
  const [user, setUser] = useState(null);
  const [paid, setPaid] = useState(false);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await api.get('/auth/me');
        setUser(userRes.data.user);

        const paidRes = await api.post('/stripe/check-paid', { email: userRes.data.user.email });
        setPaid(paidRes.data.paid);

        const campaignRes = await api.get('/campaign/my');
        setCampaigns(campaignRes.data);
      } catch (err) {
        console.error('Error fetching dashboard data', err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);


  useEffect(() => {
    const fetchData = async () => {
      try {
        const userRes = await api.get('/auth/me');
        setUser(userRes.data.user);

        const paidRes = await api.post('/stripe/check-paid', { email: userRes.data.user.email });
        setPaid(paidRes.data.paid);

        const campaignRes = await api.get('/campaign/my');
        setCampaigns(campaignRes.data);
      } catch (err) {
        console.error('Error fetching dashboard data', err);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, []);

  const refreshData = async () => {
    setLoading(true);
    try {
      const campaignRes = await api.get('/campaign/my');
      setCampaigns(campaignRes.data);
    } catch (err) {
      console.error('Error refreshing campaign data', err);
    } finally {
      setLoading(false);
    }
  };

  if (loading) return <p className="p-6">Loading...</p>;

  return (
    <div className="p-6 max-w-3xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">Dashboard</h1>
      {user && (
        <div className="mb-6">
          <p><strong>Name:</strong> {user.name}</p>
          <p><strong>Email:</strong> {user.email}</p>
          <p><strong>Paid:</strong> {paid ? 'Yes' : 'No'}</p>
        <p><strong>Campaign Submitted:</strong> {campaigns.length > 0 ? 'Yes' : 'No'}</p>
        <button
          onClick={refreshData}
          className="ml-4 bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded"
        >
          Refresh
        </button>
      </div>
    )}
    {campaigns.length > 0 ? (
      campaigns.map((campaign) => (
        <div key={campaign._id} className="bg-white rounded shadow p-4 mb-4">
          <h2 className="text-xl font-semibold mb-2">Your Campaign Details</h2>
          <p><strong>Full Name:</strong> {campaign.fullName}</p>
          <p><strong>Email:</strong> {campaign.email}</p>
          <p><strong>Role:</strong> {campaign.role}</p>
          {campaign.beneficiaryName && <p><strong>Beneficiary:</strong> {campaign.beneficiaryName}</p>}
          <p><strong>Goal Amount:</strong> ${campaign.goalAmount}</p>
          <p><strong>Donation Link:</strong> <a className="text-blue-500 underline" href={campaign.donationLink} target="_blank" rel="noreferrer">{campaign.donationLink}</a></p>
          {campaign.videoLink && <p><strong>Video Link:</strong> <a className="text-blue-500 underline" href={campaign.videoLink} target="_blank" rel="noreferrer">{campaign.videoLink}</a></p>}
          <p><strong>Status:</strong> {campaign.status}</p>

          <div className="mt-4">
            <p><strong>Uploaded Files:</strong></p>
            <ul className="list-disc ml-6">
              <li><FileViewer fileUrl={`http://localhost:5000/api/uploads/${campaign.files.idDoc}`} fileName="ID Document">ID Document</FileViewer></li>
              <li><FileViewer fileUrl={`http://localhost:5000/api/uploads/${campaign.files.billDoc}`} fileName="Utility Bill">Utility Bill</FileViewer></li>
              <li><FileViewer fileUrl={`http://localhost:5000/api/uploads/${campaign.files.campaignDoc}`} fileName="Campaign Word Doc">Campaign Word Doc</FileViewer></li>
            </ul>
          </div>
        </div>
      ))
    ) : (
      <p>No campaign submitted yet.</p>
    )}
    </div>
  );
};

export default Dashboard;
