import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import logo from"./logo.png"

const Navbar = () => {
  const { user, logout } = useAuth();

  return (
    <nav className="bg-gradient-to-r from-purple-700 via-indigo-700 to-blue-700 p-4 text-white flex justify-between items-center shadow-lg">
      <div className="flex items-center space-x-4">
        <Link to="/" className="flex items-center space-x-2">
          <img
            src={logo}
            alt="No Tip Fund Raiser"
            className="h-12 w-8"
          />
          <span className="text-xl font-bold cursor-pointer">No Tip Fund Raiser</span>
        </Link>
      </div>
      <div>
        {user ? (
          <div className="flex items-center space-x-4">
            <span>Welcome, {user.name}</span>
            <Link to="/dashboard" className="hover:underline">Dashboard</Link>
            {user.role === 'admin' && (
              <Link to="/admin" className="hover:underline">Admin Dashboard</Link>
            )}
            <button
              onClick={logout}
              className="bg-red-600 hover:bg-red-700 px-3 py-1 rounded text-white"
            >
              Logout
            </button>
          </div>
        ) : (
          <>
            <Link to="/login" className="mr-4 hover:underline">Login</Link>
            <Link to="/register" className="hover:underline">Register</Link>
          </>
        )}
      </div>
    </nav>
  );
};

export default Navbar;
